#!/bin/bash

cd "$(dirname "$BASH_SOURCE")" \
  && source '../../utils.sh'

# - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -

set_spectacle_preferences() {

  # Enables Assistive Access, see http://git.io/vcGqX
  sql_command="INSERT or REPLACE INTO access VALUES ('kTCCServiceAccessibility', 'com.divisiblebyzero.Spectacle', 0, 1, 0, NULL, NULL);"
  execute 'sudo sqlite3 /Library/Application\ Support/com.apple.TCC/TCC.db "$sql_command"' \
    'Enable Assistive Access'

  # To output the current preferences, see http://git.io/vcGJ4
  execute 'defaults write com.divisiblebyzero.Spectacle MakeLarger -data ' \
    'Set ‘Make Larger’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MakeSmaller -data ' \
    'Set ‘Make Smaller shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToBottomHalf -data ' \
    'Set ‘Move To Bottom Half’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToCenter -data ' \
    'Set ‘Move To Center’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToFullscreen -data ' \
    'Set ‘Move To Fullscreen’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToLeftHalf -data ' \
    'Set ‘Move To Left Half’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToLowerLeft -data ' \
    'Set ‘Move To Lower Left’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToLowerRight -data ' \
    'Set ‘Move To Lower Right’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToNextDisplay -data ' \
    'Set ‘Move To Next Display’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToNextThird -data ' \
    'Set ‘Move To Next Third’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToPreviousDisplay -data ' \
    'Set ‘Move To Previous Display’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToPreviousThird -data ' \
    'Set ‘Move To Previous Third’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToRightHalf -data ' \
    'Set ‘Move To Right Half’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToTopHalf -data ' \
    'Set ‘Move To Top Half’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToUpperLeft -data ' \
    'Set ‘Move To Upper Left’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle MoveToUpperRight -data ' \
    'Set ‘Move To Upper Right’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle RedoLastMove -data ' \
    'Set ‘Redo Last Move’ shortcut'
  execute 'defaults write com.divisiblebyzero.Spectacle UndoLastMove -data ' \
    'Set ‘Undo Last Move’ shortcut'

}

# - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -

main() {

  print_in_purple '\n  Spectacle\n\n'
  set_spectacle_preferences

  killall 'Spectacle' &> /dev/null

}

main
